import { DIRECTION_RIGHT, DIRECTION_LEFT, DIRECTION_DOWN, DIRECTION_UP } from '@any-touch/shared';

var getVLength = (function (v) {
    return Math.sqrt(v.x * v.x + v.y * v.y);
});

